		formData.put("fluigPedido3",hAPI.getCardValue("fluigPedido3"));
		formData.put("CCOD2",hAPI.getCardValue("CCOD3"));
		
		var users = new java.util.ArrayList();
		log.info("indexes.length "+indexes.length)
		for(var i=1; i<=indexes.length; i++){
