			var checkbox = hAPI.getCardValue("escolhido3___"+i) == "on" ? true : false;
			log.info("checkbox valor : "+hAPI.getCardValue("escolhido3___"+i))
			log.info("checkbox : "+checkbox);
			if(checkbox){
				log.info("Pedido "+hAPI.getCardValue("codproduto___"+i));
				
				formData.put("descricao___"+i,hAPI.getCardValue("descricao3___"+i));
				formData.put("fornecedor___"+i,hAPI.getCardValue("CNOME3"));
